document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".nav-item");e.forEach(function(e){const t=e.querySelector(".nav-item-inner"),n=e.querySelectorAll(".nav-dropdown-item");t.addEventListener("click",function(){e.classList.toggle("is-active")}),n.forEach(function(e){const t=e.querySelector("span");t.addEventListener("click",function(){e.classList.toggle("is-active")})})})});